readList=[[i, [int(a) for a in open(i.lower()+".txt").read().splitlines()]] for i in ["Easy", "Medium", "Hard"]];open("stats.txt","w").write("".join([f"{(difficulty:=info[0])} Difficulty\n{'-'*78}\nRuns Documented: {(Length:=len(times:=info[1]))}\nFastest Time: {min(times)}\nAverage Time: {(total:=sum(times))/Length}\nTotal Time Spent Playing {difficulty} Difficulty:\n{(timeFunc:=(lambda x: ''.join(['{} day{} '.format((days:=(x//i)),(test:=(lambda x:'s'*(1-(x==1))))(days)) if j==0 else '{} {}{} '.format((unit:=((x%[86400,3600,60,1][j-1])//i)), ['hour', 'minute', 'second'][j-1], test(unit)) for j,i in enumerate([86400,3600,60,1])])))(total)}\n\n" for info in readList])+f"Total Time On Minesweeper: {timeFunc(sum([sum(i[1]) for i in readList]))}")
